import groovy.json.JsonSlurper
import java.lang.*

// To check status code
def status = messageExchange.responseHeaders["#status#"].toString()
def statusCode= status.substring(10,14).toInteger()
log.info statusCode
assert statusCode==200
// To store the access token Value
def response = messageExchange.getResponseContent()
log.info response 
def holder = new JsonSlurper().parseText(response)
def token = holder.data.access_token
context.testCase.testSuite.project.setPropertyValue("token" ,"Bearer "+token)

