con:config.script:
import jxl.*
import eviware.soapui.model.*

filePath = "C:/Users/NymishaNunna/Desktop/API Testing Program/Project/Rest EndPoints/CustomerAndProductDetails.xls"

File Excel = new File(filePath)

//Excel contains woekbook and workbook contains sheets
Workbook wb = Workbook.getWorkbook(Excel)
//fetch the sheet

Sheet sh =wb.getSheet("Sheet1")

// Getting row and column sizes

rowSize =sh.getRows().toInteger()
columnSize =sh.getColumns().toInteger()

log.info " Total number of rows " + rowSize
log.info " Total number of columns " + columnSize
def totalstep_count = 0

for(def i=1;i<rowSize; i++)
{

		email =sh.getCell(0,i).getContents()
		product_key =sh.getCell(1,i).getContents()
		log.info email
		log.info product_key
	testRunner.testCase.testSuite.testCases['E2E_Ordering_TC30'].setPropertyValue("email",email)
	testRunner.testCase.testSuite.testCases['E2E_Ordering_TC30'].setPropertyValue("product_key",product_key)

	def tc = testRunner.testCase.testSuite.getTestCaseByName('E2E_Ordering_TC30')
def runner=tc.run(null,false)

log.info "Status : $runner.status, time taken for testcase is $runner.timeTaken ms"

// number of test steps execuetd
 log.info "number of tets steps executed are : " +runner.results.size()
//Total number teststeps Executed
 totalstep_count =totalstep_count+runner.results.size()
}

log.info totalstep_count

import groovy.sql.Sql
import groovy.lang.*

//connection Driver, username password, URL

def url ='jdbc:mysql://localhost:3306/rest';

def user='root'
def password='root'
def driver ='com.mysql.jdbc.Driver'

// Register to MySQL  JDBC Driver - For Groovy to send and Recieve

com.eviware.soapui.support.GroovyUtils.registerJdbcDriver(driver)

//connect to SQL Instance

def sql = Sql.newInstance(url,user,password,driver)

sql.eachRow("select * from customerProduct;")
{
	row ->
	def email ="$row.email";
	def product_key ="$row.prod_name";
	log.info email;
	log.info product_key;

	testRunner.testCase.testSuite.testCases['E2E_Ordering_TC30'].setPropertyValue("email",email)
     testRunner.testCase.testSuite.testCases['E2E_Ordering_TC30'].setPropertyValue("product_key",product_key)

   def tc=  testRunner.testCase.testSuite.testCases['E2E_Ordering_TC30']
   tc.run(null, false)

}

sql.close()
